// SPDX-License-Identifier: MIT
pragma solidity ^0.8.10;

import "../Interfaces/IStrategy.sol";

contract SimpleStrategy is IStrategy {
    mapping(address => uint256) private balances;
    uint256 private apy = 5;  // 固定5%收益

    function deposit(address _token, uint256 _amount) external override {
        // 模拟存款逻辑：增加用户资金记录
        balances[_token] += _amount;
    }

    function withdraw(address _token, uint256 _amount) external override {
        require(balances[_token] >= _amount, "Insufficient balance");
        balances[_token] -= _amount;
    }

    function getAPY(address _token) external view override returns (uint256) {
        return apy;
    }

    function getTotalBalance(address _token) external view override returns (uint256) {
        return balances[_token];
    }

    function invest(address _token, uint256 _amount) external override {
        // 简化处理：视为立即投资（模拟逻辑）
        balances[_token] += _amount;
    }
}

contracts/Strategies/StrategyFactory.sol.content:
//SPDX-License-Identifier: MIT

pragma solidity ^0.8.10;

import "../Interfaces/IStrategy.sol";

contract StrategyFactory {

    mapping(address => StrategyInfo[]) public tokenStrategies;
    struct StrategyInfo {
        address strategy;
        bool isActive;
    }

    mapping(address => bool) public isSupportedToken;

    function addStrategy(address _token, address _strategy) external {
        tokenStrategies[_token].push(StrategyInfo(_strategy, true));
        //tokenStrategies[token] = IStrategy(strategy);
    }

    function isValidStrategy(address _strategy) external view returns (bool) {
    // 遍历所有 token 的策略，检查是否存在这个 _strategy 并且是有效的
    for (uint256 i = 0; i < tokenStrategies[_strategy].length; i++) {
        if (tokenStrategies[_strategy][i].strategy == _strategy && tokenStrategies[_strategy][i].isActive) {
            return true;
        }
    }
    return false;
}

    function removeStrategy(address _token, address _strategy) external {
    for (uint256 i = 0; i < tokenStrategies[_token].length; i++) {
        if (tokenStrategies[_token][i].strategy == _strategy) {
            tokenStrategies[_token][i].isActive = false;
            break;
        }
    }
    }

    function getBestStrategy(address _token) public view returns (address) {
        uint256 bestAPY = 0;
        address bestStrategy;

        for (uint256 i = 0; i < tokenStrategies[_token].length; i++) {
            StrategyInfo storage strategyInfo = tokenStrategies[_token][i];
            if (strategyInfo.isActive) {
                uint256 apy = IStrategy(strategyInfo.strategy).getAPY(_token);
                if (apy > bestAPY) {
                    bestAPY = apy;
                    bestStrategy = strategyInfo.strategy;
                }
            }
        }
        return bestStrategy;
    }
    
}

contracts/Strategies/StrategyManager.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.10;

import "../Interfaces/IStrategy.sol";
import "../Interfaces/IStrategyFactory.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract StrategyManager is Ownable {
    address public vipUser;
    uint256 public feeRate;

    struct UserPortfolio {
        address[] strategies; // 用户选择的策略
        mapping(address => uint256) allocations;  // 每个策略的分配比例
    }

    mapping(address => UserPortfolio) private userPortfolios;  // 记录每个用户的策略组合
    IStrategyFactory public strategyFactory;  // 关联的策略工厂

    event StrategyAssigned(address indexed user, address strategy, uint256 allocation);
    event StrategyRebalanced(address indexed user, address[] strategies, uint256[] allocations);

    constructor(address _strategyFactory, address _vipUser, uint256 _feeRate) Ownable(msg.sender) {
        strategyFactory = IStrategyFactory(_strategyFactory);
        vipUser = _vipUser;
        feeRate = _feeRate;
    }

    // 用户绑定策略和资金分配
    function assignStrategy(address _strategy, uint256 _allocation) external {
        require(strategyFactory.isValidStrategy(_strategy), "Invalid strategy");
        UserPortfolio storage portfolio = userPortfolios[msg.sender];
        portfolio.strategies.push(_strategy);
        portfolio.allocations[_strategy] = _allocation;
        emit StrategyAssigned(msg.sender, _strategy, _allocation);
    }

    // 调整策略组合（再平衡）
    function rebalance(address[] calldata _strategies, uint256[] calldata _allocations) external {
    require(_strategies.length == _allocations.length, "Mismatched arrays");

    uint256 totalAllocation = 0;
    for (uint256 i = 0; i < _allocations.length; i++) {
        totalAllocation += _allocations[i];
    }
    require(totalAllocation == 100, "Allocations must sum to 100");

    UserPortfolio storage portfolio = userPortfolios[msg.sender];

    // 先清空之前的策略
    delete portfolio.strategies;

    // 重新分配策略
    for (uint256 i = 0; i < _strategies.length; i++) {
        require(strategyFactory.isValidStrategy(_strategies[i]), "Invalid strategy");
        portfolio.strategies.push(_strategies[i]);
        portfolio.allocations[_strategies[i]] = _allocations[i];
    }

    emit StrategyRebalanced(msg.sender, _strategies, _allocations);
}

}

contracts/Strategies/StrategyManagerFact.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.10;

import "./StrategyManager.sol";

contract StrategyManagerFactory {
    address public owner;
    mapping(address => address) public vipStrategyManager; // VIP 用户地址 => 他们的 StrategyManager

    event StrategyManagerDeployed(address indexed vip, address strategyManager);

    modifier onlyOwner() {
        require(msg.sender == owner, "Not owner");
        _;
    }
    constructor() {
        owner = msg.sender;
    }

    function deployStrategyManager(
    address _strategyFactory,  // 新增这个参数
    address _vipUser,
    uint256 _feeRate
) external onlyOwner returns (address) {
    require(vipStrategyManager[_vipUser] == address(0), "Already deployed");

    // 现在构造函数参数匹配了
    StrategyManager strategyManager = new StrategyManager(_strategyFactory, _vipUser, _feeRate);
    
    vipStrategyManager[_vipUser] = address(strategyManager);

    emit StrategyManagerDeployed(_vipUser, address(strategyManager));
    return address(strategyManager);
}

    function getVIPStrategyManager(address _vipUser) external view returns (address) {
        return vipStrategyManager[_vipUser];
    }
}

